import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Heading,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Button,
  Badge,
  Flex,
  Text,
  Spinner,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalCloseButton,
  VStack,
  Divider,
  Alert,
  AlertIcon,
} from '@chakra-ui/react';
import {  fetchOrderItems } from '../api';

const Order = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [orderItems, setOrderItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingDetails, setLoadingDetails] = useState(false);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const navigate = useNavigate();

  useEffect(() => {
    const getOrders = async () => {
      try {
        setLoading(true);
        const fetchedOrders = await fetchOrders();
        setOrders(fetchedOrders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      } finally {
        setLoading(false);
      }
    };
    getOrders();
  }, []);

  const handleViewDetails = async (order) => {
    setSelectedOrder(order);
    setLoadingDetails(true);
    try {
      const items = await fetchOrderItems(order.id);
      setOrderItems(items);
    } catch (error) {
      console.error('Error fetching order items:', error);
      setOrderItems([]);
    } finally {
      setLoadingDetails(false);
      onOpen();
    }
  };

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'completed':
        return 'green';
      case 'processing':
        return 'blue';
      case 'cancelled':
        return 'red';
      case 'pending':
      default:
        return 'yellow';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const options = { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <Box p={8} textAlign="center">
        <Spinner size="xl" />
        <Text mt={4}>Loading orders...</Text>
      </Box>
    );
  }

  if (orders.length === 0) {
    return (
      <Box p={8}>
        <Heading as="h1" mb={6}>Your Orders</Heading>
        <Alert status="info" mb={4}>
          <AlertIcon />
          You haven't placed any orders yet.
        </Alert>
        <Button colorScheme="blue" onClick={() => navigate('/')}>
          Browse Menu
        </Button>
      </Box>
    );
  }

  return (
    <Box p={4}>
      <Heading as="h1" mb={6}>Your Orders</Heading>

      <Table variant="simple">
        <Thead>
          <Tr>
            <Th>Order ID</Th>
            <Th>Date</Th>
            <Th>Total</Th>
            <Th>Status</Th>
            <Th>Action</Th>
          </Tr>
        </Thead>
        <Tbody>
          {orders.map((order) => (
            <Tr key={order.id}>
              <Td>#{order.id}</Td>
              <Td>{formatDate(order.created_at || order.pickup_time)}</Td>
              <Td>Rs.{order.total_amount?.toFixed(2) || '0.00'}</Td>
              <Td>
                <Badge colorScheme={getStatusColor(order.status)}>
                  {order.status || 'Pending'}
                </Badge>
              </Td>
              <Td>
                <Button 
                  size="sm" 
                  colorScheme="blue"
                  onClick={() => handleViewDetails(order)}
                >
                  View Details
                </Button>
              </Td>
            </Tr>
          ))}
        </Tbody>
      </Table>

      {/* Order Details Modal */}
      <Modal isOpen={isOpen} onClose={onClose} size="lg">
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Order #{selectedOrder?.id} Details</ModalHeader>
          <ModalCloseButton />
          <ModalBody pb={6}>
            {loadingDetails ? (
              <Flex justify="center" align="center" py={8}>
                <Spinner />
              </Flex>
            ) : (
              <>
                <Flex justify="space-between" mb={4}>
                  <Box>
                    <Text fontWeight="bold">Order Date</Text>
                    <Text>{formatDate(selectedOrder?.created_at || selectedOrder?.pickup_time)}</Text>
                  </Box>
                  <Box>
                    <Text fontWeight="bold">Status</Text>
                    <Badge colorScheme={getStatusColor(selectedOrder?.status)}>
                      {selectedOrder?.status || 'Pending'}
                    </Badge>
                  </Box>
                </Flex>

                {selectedOrder?.pickup_time && (
                  <Box mb={4}>
                    <Text fontWeight="bold">Pickup Time</Text>
                    <Text>{formatDate(selectedOrder.pickup_time)}</Text>
                  </Box>
                )}

                <Divider my={4} />

                <Heading as="h3" size="sm" mb={3}>Items</Heading>

                {orderItems.length === 0 ? (
                  <Text color="gray.500">No items found for this order.</Text>
                ) : (
                  <VStack align="stretch" spacing={2}>
                    {orderItems.map((item, index) => (
                      <Flex key={index} justify="space-between">
                        <Text>
                          {item.name || item.item_name} x {item.quantity}
                        </Text>
                        <Text fontWeight="medium">
                          Rs.{((item.price || 0) * (item.quantity || 1)).toFixed(2)}
                        </Text>
                      </Flex>
                    ))}

                    <Divider my={2} />

                    <Flex justify="space-between" fontWeight="bold">
                      <Text>Total:</Text>
                      <Text>Rs.{selectedOrder?.total_amount?.toFixed(2) || '0.00'}</Text>
                    </Flex>
                  </VStack>
                )}

                {selectedOrder?.customer_name && (
                  <>
                    <Divider my={4} />
                    <Heading as="h3" size="sm" mb={3}>Customer Information</Heading>
                    <Text><strong>Name:</strong> {selectedOrder.customer_name}</Text>
                    {selectedOrder.customer_email && (
                      <Text><strong>Email:</strong> {selectedOrder.customer_email}</Text>
                    )}
                    {selectedOrder.customer_phone && (
                      <Text><strong>Phone:</strong> {selectedOrder.customer_phone}</Text>
                    )}
                    {selectedOrder.customer_address && (
                      <Text><strong>Address:</strong> {selectedOrder.customer_address}</Text>
                    )}
                  </>
                )}
              </>
            )}
          </ModalBody>
        </ModalContent>
      </Modal>
    </Box>
  );
};

export default Order;
