"use client"

import { useEffect, useState } from "react"
import {
  Box,
  Grid,
  Heading,
  Card,
  CardBody,
  CardFooter,
  Image,
  Stack,
  Text,
  Button,
  Spinner,
  Center,
  Alert,
  AlertIcon,
  CloseButton,
  useDisclosure,
} from "@chakra-ui/react"
import { fetchMenu } from "../api"
import { useCart } from "../hooks/useCart"
import { useToast } from "../hooks/use-toast"

const Menu = () => {
  const [menuItems, setMenuItems] = useState([])
  const [loading, setLoading] = useState(true)
  const { addItem, items } = useCart()  // Also get items for debugging
  const { toast } = useToast()
  const { isOpen, onOpen, onClose } = useDisclosure()
  const [addedItem, setAddedItem] = useState(null)

  // Fetch menu items on component mount
  useEffect(() => {
    const getMenu = async () => {
      try {
        const data = await fetchMenu()
        setMenuItems(data)
      } catch (error) {
        console.error("Failed to fetch menu:", error)
      } finally {
        setLoading(false)
      }
    }

    getMenu()
  }, [])

  // Format currency
  const formatPrice = (price) => {
    return `Rs.${(price / 100).toFixed(2)}`
  }

  // Handle adding item to cart with debug logging
  const handleAddToCart = (item) => {
    console.log("Adding item to cart:", item)
    console.log("Item ID:", item.id)
    console.log("Item price (original):", item.price)

    // Pass the entire menu item object to addItem
    addItem(item)
    setAddedItem(item)

    // For debugging: log cart items after adding
    setTimeout(() => {
      console.log("Current cart items after adding:", items)
    }, 100)

    // Show toast notification
    toast({
      title: `${item.name} added to cart`,
      variant: "default",
    })

    // Auto close the alert after 3 seconds
    onOpen()
    setTimeout(() => {
      onClose()
    }, 3000)
  }

  if (loading) {
    return (
      <Center py={10}>
        <Spinner size="xl" />
      </Center>
    )
  }

  return (
    <Box>
      <Heading as="h1" mb={6}>
        Our Menu
      </Heading>

      {/* Debug cart items - remove in production */}
      <Box mb={4} p={4} bg="gray.100" borderRadius="md">
        <Text fontWeight="bold">Current Cart (Debug):</Text>
        {items.length === 0 ? (
          <Text>Cart is empty</Text>
        ) : (
          items.map((item, index) => (
            <Text key={index}>
              ID: {item.menuItemId} | {item.name} | Qty: {item.quantity} | Price: {item.price}
            </Text>
          ))
        )}
      </Box>

      {/* Item added alert */}
      {isOpen && addedItem && (
        <Alert status="success" mb={4} rounded="md">
          <AlertIcon />
          <Text>{addedItem.name} added to your cart!</Text>
          <CloseButton position="absolute" right="8px" top="8px" onClick={onClose} />
        </Alert>
      )}

      <Grid templateColumns={{ base: "1fr", md: "repeat(2, 1fr)", lg: "repeat(3, 1fr)" }} gap={6}>
        {menuItems.map((item) => (
          <Card key={item.id} maxW="sm" shadow="md" border="1px" borderColor="gray.200">
            <CardBody>
              <Image src={item.imageUrl} alt={item.name} borderRadius="lg" />
              <Stack mt="4" spacing="3">
                <Heading size="md">{item.name}</Heading>
                <Text color="gray.700" noOfLines={2}>
                  {item.description}
                </Text>
                <Text color="blue.600" fontSize="xl" fontWeight="bold">
                  {formatPrice(item.price)}
                </Text>
              </Stack>
            </CardBody>
            <CardFooter>
              <Button
                colorScheme="blue"
                width="full"
                onClick={() => handleAddToCart(item)}
              >
                Add to Cart (ID: {item.id})
              </Button>
            </CardFooter>
          </Card>
        ))}
      </Grid>
    </Box>
  )
}

export default Menu
