"use client";

import { createContext, useCallback, useEffect, useMemo, useReducer } from 'react';

const initialState = {
  items: [],
};

function cartReducer(state, action) {
  console.log('Cart reducer action:', action.type, action.payload);
  console.log('Current state before action:', state);

  switch (action.type) {
    case 'ADD_ITEM': {
      const existingItemIndex = state.items.findIndex(
        (item) => item.ItemId === action.payload.menuItemId
      );

      if (existingItemIndex > -1) {
        const updatedItems = [...state.items];
        updatedItems[existingItemIndex] = {
          ...updatedItems[existingItemIndex],
          quantity: updatedItems[existingItemIndex].quantity + 1,
        };
        return { ...state, items: updatedItems };
      } else {
        const newItem = {
          menuItemId: action.payload.menuItemId,
          name: action.payload.name,
          price: action.payload.price,
          quantity: 1,
        };
        return { ...state, items: [...state.items, newItem] };
      }
    }

    case 'REMOVE_ITEM': {
      const updatedItems = state.items.filter(
        (item) => item.menuItemId !== action.payload.menuItemId
      );
      return { ...state, items: updatedItems };
    }

    case 'UPDATE_QUANTITY': {
      const { menuItemId, quantity } = action.payload;

      if (quantity <= 0) {
        return cartReducer(state, {
          type: 'REMOVE_ITEM',
          payload: { menuItemId }
        });
      }

      const updatedItems = state.items.map((item) =>
        item.menuItemId === menuItemId ? { ...item, quantity } : item
      );

      return { ...state, items: updatedItems };
    }

    case 'CLEAR_CART':
      return initialState;

    default:
      return state;
  }
}

export const CartContext = createContext(undefined);

export function CartProvider({ children }) {
  const [state, dispatch] = useReducer(cartReducer, initialState, () => {
    try {
      if (typeof window !== 'undefined') {
        const savedCart = localStorage.getItem('cart');
        if (savedCart) {
          return { items: JSON.parse(savedCart) };
        }
      }
    } catch (e) {
      console.error('Error loading cart from localStorage:', e);
    }
    return initialState;
  });

  useEffect(() => {
    try {
      if (typeof window !== 'undefined') {
        localStorage.setItem('cart', JSON.stringify(state.items));
      }
    } catch (e) {
      console.error('Error saving cart to localStorage:', e);
    }
  }, [state.items]);

  const addItem = useCallback((item) => {
    dispatch({ type: 'ADD_ITEM', payload: item });
  }, []);

  const removeItem = useCallback((menuItemId) => {
    dispatch({ type: 'REMOVE_ITEM', payload: { menuItemId } });
  }, []);

  const updateQuantity = useCallback((menuItemId, quantity) => {
    dispatch({ type: 'UPDATE_QUANTITY', payload: { menuItemId, quantity } });
  }, []);

  const clearCart = useCallback(() => {
    dispatch({ type: 'CLEAR_CART' });
  }, []);

  const totalPrice = useMemo(() => {
    return state.items.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
  }, [state.items]);

  const totalItems = useMemo(() => {
    return state.items.reduce((total, item) => total + item.quantity, 0);
  }, [state.items]);

  const value = useMemo(() => ({
    items: state.items,
    addItem,
    removeItem,
    updateQuantity,
    clearCart,
    totalPrice,
    totalItems,
  }), [
    state.items,
    addItem,
    removeItem,
    updateQuantity,
    clearCart,
    totalPrice,
    totalItems
  ]);

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
}
