import React, { useEffect, useState } from 'react';
import { fetchMenu } from '../api';
import {CardFooter, Image, CardHeader, Card , CardTitle, CardDescription, Button } from '@chakra-ui/react';

const Menu = () => {
  const [menu, setMenu] = useState([]);

  useEffect(() => {
    const getMenu = async () => {
      const menuItems = await fetchMenu();
      setMenu(menuItems);
    };
    getMenu();
  }, []);

  return (
    <Card className="flex flex-col overflow-hidden rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300">
       <div className="relative h-48 w-full">
         <Image
           src={item.imageUrl || 'https://picsum.photos/300/200'} // Fallback image
           alt={item.name}
           fill
           style={{ objectFit: 'cover' }}
           sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
           priority={false} // Only set priority for above-the-fold images if needed
           onError={(e) => (e.currentTarget.src = 'https://picsum.photos/300/200')} // Handle image loading errors
         />
       </div>
      <CardHeader className="p-4">
        <CardTitle className="text-lg font-semibold">{item.name}</CardTitle>
        <CardDescription className="text-sm text-muted-foreground">{item.description}</CardDescription>
      </CardHeader>
      <CardContent className="p-4 pt-0 flex-grow">
        {/* Content can be added here if needed, e.g., ingredients */}
      </CardContent>
      <CardFooter className="flex justify-between items-center p-4 pt-0 mt-auto">
        <span className="text-lg font-bold text-primary">
          ${item.price.toFixed(2)}
        </span>
        <Button onClick={handleAddToCart} size="sm" aria-label={`Add ${item.name} to cart`}>
           <PlusCircle className="mr-2 h-4 w-4" /> Add to Cart
        </Button>
      </CardFooter>
    </Card>
    // <div>
    //   <h2>Menu</h2>
    //   <ul>
    //     {menu.map((item) => (
    //       <li key={item.id}>
    //         {item.name} - ${item.price}
    //       </li>
    //     ))}
    //   </ul>
    // </div>  
  );
};

export default Menu;
